import { SET_EMPLOYEES, DELETE_EMPLOYEE, ADD_EMPLOYEE } from "./types";
import employeeApi from "../../api/employeeApi";
import { getAlerts } from "./alertAct"
import { useNavigate } from "react-router-dom"
//getAll list Employee
export const setEmployee = (items) => ({
  type: SET_EMPLOYEES,
  payload: items,
});

// fetch all Employee
export const fetchEmployees = () => async (dispatch) => {
  employeeApi.getAll()
    .then(res => dispatch(setEmployee(res)))
    .catch(err => dispatch(getAlerts({ msg: "disconnect fetch", type: 'error' })))
};
// Delete employee
export const deleteEmployee = (id) => (dispatch) => {
  employeeApi.delete(id)
    .then((res) => {
      dispatch(getAlerts({ msg: `Success delete`, title: 'Success' }))
      dispatch({ type: DELETE_EMPLOYEE, payload: id, })
    })
    .catch(err => dispatch(getAlerts({ msg: err.response.statusText, type: 'error' })));
}

// add employee
export const addEmployee = (employee) => (dispatch) => {
  employeeApi.post(employee)
    .then(res => {
      const navigate = useNavigate()
      dispatch(getAlerts({ msg: `Success create employee id ${res.id}`, title: 'Success' }))
      navigate('/employee')
      dispatch({
        type: ADD_EMPLOYEE,
        payload: res
      });
    })
    .catch(err => {
      dispatch(getAlerts({ msg: err.response.statusText, title: 'Error' }))
    });
}
