import { SET_ACCOUNT } from '../actions/types'

const initialState = {
    detail: null,
    list: [],
}

export default function accountReducer(state = initialState, action) {
    switch (action.type) {
        case SET_ACCOUNT: {
            const newList = [...action.payload]
            return {
                ...state,
                list: newList,
            };
        }
        default:
            return state;
    }

}
